import iofrom typing import Unionimport aspose.words as awimport docxfrom .ooxml import DocHandlerfrom .export_html import DocHTMLfrom .export_json import DocJSONdef doc_to_docx(in_stream: io.BytesIO, out_stream: io.BytesIO):    """    Converts a .doc file to a .docx file using Aspose.Words.    Args:        in_stream (io.BytesIO): The input stream containing the .doc file.        out_stream (io.BytesIO): The output stream to write the .docx file.    """    doc = aw.Document(in_stream)    doc.save(out_stream, aw.SaveFormat.DOCX)def docx_to_html(docx_path: Union[str, io.BytesIO]) -> tuple:    """    Converts a DOCX document to HTML.        Args:        docx_path (str): The path to the DOCX file.        Returns:        tuple: A tuple containing the HTML content and table of contents links.    """    doc = docx.Document(docx_path)    handler = DocHandler(doc)    converter = DocHTML()    return converter.get_html(handler)def docx_to_json(docx_path: Union[str, io.BytesIO]) -> str:    """    Converts a DOCX document to JSON.        Args:        docx_path (str): The path to the DOCX file.        Returns:        str: Formatted JSON content.    """    doc = docx.Document(docx_path)    handler = DocHandler(doc)    converter = DocJSON()    return converter.get_json(handler)